
public class Problem1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=1;
		int total1 = 0;
		
		
		for(i=1;i<1000;i++) {
	
	
				
					if(i%3==0 || i%5==0) {
					System.out.println("Multiples of 3 and 5 : "+i);
					
					total1=total1+i;
					}
					
		}
		
	
		System.out.println("Addition: "+total1);
		
		
	
	

}
}

					
					
					
				
		
				
		
			
	
		
	

		
			
			

	


